import React, {useState, useRef, useEffect} from "react";
import ReactHover from 'react-hover';
import { makeStyles } from "@material-ui/core/styles";
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import Arrowr from "@material-ui/icons/Input";

import styles from "assets/jss/material-kit-react/views/componentsSections/newsStyle.js";

import Bgvideo from "assets/videos/warrovervidf.mp4";

const useStyles = makeStyles(styles);


export default function SectionNews() {
    const classes = useStyles();

    return(<div>
        <div className={classes.container}>
            <h1 className={classes.title}>
                NEWS
            </h1>
            <video autoPlay loop muted className={classes.video}>
                <source src={Bgvideo} type="video/mp4" />
            </video>
            <div className={classes.section1}>
                <h2 className={classes.wrapper}>Indian Rover Challenge 2020</h2>
            </div>
            <div className={classes.section2}>
                <h2 className={classes.wrapper}>Update on URC 2020 due to COVID-19</h2>
            </div>   
            <div className={classes.section3}>
                <h2 className={classes.wrapper} style={{marginTop: '40vh'}}>Sponsored for 3D printers by ANET</h2>
            </div>
        </div>
    </div>
    );
}
