import { container } from "assets/jss/material-kit-react.js";

const componentsStyle = {

  container,
  brand: {
    position: 'absolute',
    color: "#ffffff",
    zIndex: "1000",
    marginLeft: "30vw",
    marginTop: "2vh",
  },
  '@keyframes pulse': {
    from: {transform: 0.95},
    to: {transform: 1},
  },

  '@media (max-width: 800px)': {
    achieve:{
      margin: '0 0 5vh 12vw',
      fontSize: '10vw'
    },

    gallery: {
      margin: '20vh 0 5vh 28vw',
      fontSize: '10vw'
    },

    team : {
      margin: '20vh 0 5vh 35vw',
      fontSize: '10vw'
    },

    mainimg: {
      clipPath: 'circle(74.2% at 50% 26%)',
      height: '30vh',
    },
    title: {
      fontFamily: "army",
      fontSize: "6vw",
      fontWeight: "600",
      display: "inline-block",
      position: "relative"
    },
    subtitle: {
      fontSize: "1.313rem",
      maxWidth: "500px",
      marginLeft: "17vw",
    },  
    mainRaised: {
      margin: "-30px 20px 0px",
      borderRadius: "6px",
      boxShadow:
        "0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.2)"
    },
  },

  '@media (min-width: 800px)': {
    achieve:{
      margin: '0 0 5vh 38vw'
    },

    gallery: {
      margin: '15vh 0 7vh 43vw'
    },

    team: {
      margin: '15vh 0 7vh 44vw'
    },

    mainimg: {
      clipPath: 'circle(74.2% at 50% 26%)',
    },

    title: {
      fontFamily: "army",
      fontSize: "6vw",
      fontWeight: "600",
      display: "inline-block",
      position: "relative"
    },
    subtitle: {
      fontSize: "1vw",
      maxWidth: "500px",
      marginLeft: "5vw"
    },
    mainRaised: {
      margin: "-60px 30px 0px",
      borderRadius: "6px",
      boxShadow:
        "0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.2)"
    },
    downvote: {
      position: 'absolute',
      marginLeft: '48vw',
      marginTop: '0vh',
      height: '50px',
      width: '50px',
      zIndex: '10000',
    },
    arrow: {
      color: "white",
      width: "100px",
      height: "100px",
      transition: 'ease-in-out',
	    transform: 'scale(1)',
	    animationName: '$pulse 2s infinite'
    }
  },

  main: {
    background: "#FFFFFF",
    position: "relative",
    zIndex: "3"
  },

  link: {
    textDecoration: "none"
  },
  textCenter: {
    textAlign: "center"
  },

};

export default componentsStyle;
