import React from 'react';
import cx from 'clsx';
import { makeStyles } from '@material-ui/styles';
import Box from '@material-ui/core/Box';
import Link from '@material-ui/core/Link';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Divider from '@material-ui/core/Divider';
import ArrowForwardIos from '@material-ui/icons/ArrowForwardIos';
import ModeComment from '@material-ui/icons/ModeComment';
import Favorite from '@material-ui/icons/Favorite';
import { usePushingGutterStyles } from '@mui-treasury/styles/gutter/pushing';
import { useLabelIconStyles } from '@mui-treasury/styles/icon/label';
import { useRowFlexStyles } from '@mui-treasury/styles/flex/row';

//load the medias
import img1 from "assets/img/image/rovers/2013.webp";
import img2 from "assets/img/image/rovers/2014.webp";
import img3 from "assets/img/image/rovers/2015.webp";
import img4 from "assets/img/image/rovers/2016.webp";
import img5 from "assets/img/image/rovers/2017.webp";
import img6 from "assets/img/image/rovers/2019.webp";
import img7 from "assets/img/image/rovers/2020-irc.webp";
import profilePageStyle from 'assets/jss/material-kit-react/views/profilePage';

const img = [img1, img2, img3, img4, img5, img6, img7];
const text = ['Team Rudra achieved 5th world rank and 1st Asia rank.', 'Team Rudra bagged 5th position in world and 1st in Asia.',
 'Team Rudra achieved 12th world rank and 2nd rank in Asia.', 'Team Rudra stood at 9th position in world and 1st among the Asian Teams.',
  'Team Rudra stood at 20th position in world and 4th among the Asian Teams.', 'Team Rudra stood at 11th position in world and 2nd among the Asian Teams.', 
  'Team Rudra stood at 3rd position globally and 1st among the Asian teams'];
const title = ['URC-2013', 'URC-2014', 'URC-2015', 'URC-2016', 'URC-2017', 'URC-2019', 'IRC-2020'];

const useStyles = makeStyles(() => ({
  '@media (max-width: 800px)': {
    card: {
      position: 'absolute',
      marginLeft: '9vw',
      marginTop: '10vh',
      // display: 'flex',
      height: '40vh',
      width: '60vw',
      borderRadius: 16,
    },
    media: {
      display: 'block',
      float: 'left',
      marginLeft: '5vw',
      width: '80vw',
      flexShrink: 0,
      backgroundColor: '#4f4f4f',
      borderRadius: 12,
      boxShadow: '0 2px 8px 0 #c1c9d7, 0 -2px 8px 0 #cce1e9',
    },
    content: {
      float: 'left',
      display: 'block',
      width: '80vw'
    },
    heading: {
      fontSize: 40,
      fontWeight: 'bold',
      letterSpacing: '0.5px',
      marginBottom: 0,
      marginRight: "2px",
      display: 'inline-block',
    },
    body: {
      fontSize: 25,
      color: '#8a8a8a',
    },
    divider: {
      margin: '1px, 0',
    },
    textFooter: {
      fontSize: 14,
    },
    icon: {
      fontSize: '1.2rem',
      verticalAlign: 'bottom',
    },
  },

  '@media (min-width: 800px)': {
    card: {
      position: 'absolute',
      marginLeft: '9vw',
      marginTop: '10vh',
      display: 'flex',
      height: '40vh',
      width: '60vw',
      borderRadius: 16,
    },
    media: {
      marginLeft: '5vw',
      width: '23vw',
      flexShrink: 0,
      backgroundColor: '#4f4f4f',
      borderRadius: 12,
      boxShadow: '0 2px 8px 0 #c1c9d7, 0 -2px 8px 0 #cce1e9',
    },
    content: {
      width: '20vw'
    },
    heading: {
      fontSize: 40,
      fontWeight: 'bold',
      letterSpacing: '0.5px',
      marginBottom: 0,
      marginRight: "2px",
      display: 'inline-block',
    },
    body: {
      fontSize: 25,
      color: '#8a8a8a',
    },
    divider: {
      margin: '1px, 0',
    },
    textFooter: {
      fontSize: 14,
    },
    icon: {
      fontSize: '1.2rem',
      verticalAlign: 'bottom',
    },
  }

}));

export default function SectionImageInfo(props) {
  const styles = useStyles();
  const gutterStyles = usePushingGutterStyles({ space: 1.5 });
  const labelStyles = useLabelIconStyles({ linked: true });
  const flexStyles = useRowFlexStyles();
  return (
    <Card className={styles.card} elevation={0}>
      <CardContent className={styles.content}>
        <Box mb={1}>
          <h3 className={styles.heading}>{title[props.index]}</h3>
        </Box>
        <p className={styles.body}>
          {text[props.index]}
        </p>
        <Divider className={styles.divider} light />
        <div className={flexStyles.parent}>
          {/* eslint-disable-next-line jsx-a11y/anchor-is-valid */}
        </div>
      </CardContent>
      <CardMedia
        className={styles.media}
        image={img[props.index]}
      />
    </Card>
  );
};
