import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { Parallax, Background } from 'react-parallax';
import {isMobile} from 'react-device-detect';

import Avatar from '@material-ui/core/Avatar';
import Button from "components/CustomButtons/Button.js";
import styles from "assets/jss/material-kit-react/components/teamStyle.js";
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import rahul from 'assets/img/image/teams/team19/Rahul Aggarwal.webp';
import renish from 'assets/img/image/teams/team18/Renish.JPG';
import david from 'assets/img/image/teams/team19/David Chaudhary.webp';
import chaitanya from 'assets/img/image/teams/team19/Chaitanya Joshi.webp';
import abhishek from 'assets/img/image/teams/team18/AbhishekReddy.jpeg';
import farhad from 'assets/img/image/teams/team19/Farhad Bharucha.webp';
import sri from 'assets/img/image/teams/team19/shri pic.webp';
import bgImage from 'assets/img/sign.jpg';
import bgImageMob from 'assets/img/walle.jpg';


const useStyles = makeStyles(styles);

export default function FullWidthGrid() {
  const classes = useStyles();

  bgImageRender = () =>{
    if(isMobile)
      return bgImageMob;
    else
      return bgImage;
  }

  return (
    <Parallax
    blur={{ min: -100, max: 100 }}
    bgImage={bgImageRender()}
    bgImageAlt="the cat"
    strength={700}
>
    <div className={classes.root}>
      <div className={classes.title}>
        <h1 style={{ fontSize: '10vh', color: 'white' }}>TEAM</h1>
      </div>
      <Grid container spacing={3}>
        <Grid className={classes.box} item xs={10} sm={2}>
          {/* <img className={classes.bacg} src={rahul}></img> */}
          <Avatar className={classes.imag} alt="Rahul Agarwal" src={rahul} />
          <h2 style={{ color: '#f5f3f0' }}> Rahul Agarwal</h2>
          <h4 style={{ color: '#f5f3f0' }}> Team Lead</h4>
        </Grid>
        <Grid className={classes.box} item xs={10} sm={2}>
          {/* <img className={classes.bacg} src={renish}></img> */}
          <Avatar className={classes.imag} alt="Renish Kumar" src={renish} />
          <h2 style={{ color: '#f5f3f0' }}> Renish Kumar</h2>
          <h4 style={{ color: '#f5f3f0' }}> Team Manager</h4>
        </Grid>
        <Grid className={classes.box} item xs={10} sm={2}>
          {/* <img className={classes.bacg} src={david}></img> */}
          <Avatar className={classes.imag} alt="David Chaudhary" src={david} />
          <h2 style={{ color: '#f5f3f0' }}> David Chaudhary</h2>
          <h4 style={{ color: '#f5f3f0' }}> Technical Director</h4>
        </Grid>
        <Button className={classes.btnclass} color="primary" size="lg" round>
          TEAM HISTORY
        </Button>
      </Grid>
      <Grid container spacing={3}>
        <Grid className={classes.box} item xs={10} sm={2}>
          {/* <img className={classes.bacg} src={abhishek}></img> */}
          <Avatar className={classes.imag} alt="Abhishek Reddy" src={abhishek} />
          <h2 style={{ color: '#f5f3f0' }}> Abhishek Reddy</h2>
          <h4 style={{ color: '#f5f3f0' }}> Head Of Corporate</h4>
        </Grid>
        <Grid className={classes.box} item xs={10} sm={2}>
          {/* <img className={classes.bacg} src={farhad}></img> */}
          <Avatar className={classes.imag} alt="Farhad Bharucha" src={farhad} />
          <h2 style={{ color: '#f5f3f0' }}> Farhad Bharucha</h2>
          <h4 style={{ color: '#f5f3f0' }}> Coding Lead</h4>
        </Grid>
        <Grid className={classes.box} item xs={10} sm={2}>
          {/* <img className={classes.bacg} src={sri}></img>     */}
          <Avatar className={classes.imag} alt="Shri" src={sri} />
          <h2 style={{ color: '#f5f3f0' }}> Shri Venkatesh</h2>
          <h4 style={{ color: '#f5f3f0' }}> Science Lead</h4>
        </Grid>
        <Grid className={classes.box} item xs={10} sm={2}>
          {/* <img className={classes.bacg} src={chaitanya}></img> */}
          <Avatar className={classes.imag} alt="Chaitanya Joshi" src={chaitanya} />
          <h2 style={{ color: '#f5f3f0' }}> Chaitanya Joshi</h2>
          <h4 style={{ color: '#f5f3f0' }}> Mechanical Lead</h4>
        </Grid>
      </Grid>
    </div>
    </Parallax>
  );
}
