import rudrawin from "assets/img/image/news/rudrawin2.webp";
import urcupdate from "assets/img/image/news/urc2020.webp";
import { isParenthesizedExpression } from "typescript";

const newsStyle = {
    container:{
        height: '110vh',
        width: '96vw'
    },
    video :{
        position: 'absolute',
        width: '100%', 
        zIndex: '10'
    },
    title: {
        fontFamily: 'cairoBlack',
        position: 'absolute',
        color: '#fff',
        marginTop: '5vh',
        // marginLeft: '10vw',
        fontSize: '200px',
        fontWeight: '400',
        textStrokeWidth: '5px',
        textStrokeColor: '#fff',
        textFillColor: 'transparent',
        zIndex: '20'
    },
    wrapper:{
        fontFamily: 'cairoLight',
        fontSize: '30px',
        color: 'white',
        marginLeft: '10px',
        marginTop: '250px',
    },
    section1: {
        position: 'absolute',
        marginLeft: '30vw',
        marginTop: '10vh',
        height: '35vh',
        width: '25vw',
        backgroundColor: '#000',
        zIndex: '100',
        transition: '500ms ease-in-out',
        transformOrigin: 'top left',
        backgroundImage:'url('+ rudrawin +')',
        // backgroundImage:'linear-gradient(to bottom, rgba(245, 246, 252, 0.52), rgba(117, 19, 93, 0.73))',
        backgroundSize:'cover',
        '&:hover' : {
            transform: 'scale(1.3)',
            backgroundImage:'linear-gradient(to bottom, transparent, rgba(0, 0, 0, 0.72)), url('+ rudrawin +')',
            zIndex: '200'
        },
    },
    section2: {
        position: 'absolute',
        marginLeft: '63vw',
        marginTop: '10vh',
        height: '35vh',
        width: '25vw',
        backgroundColor: '#000',
        zIndex: '100',
        transition: '500ms ease-in-out',
        transformOrigin: 'top left',
        backgroundImage:'url('+ urcupdate +')',
        // backgroundImage:'linear-gradient(to bottom, rgba(245, 246, 252, 0.52), rgba(117, 19, 93, 0.73))',
        backgroundSize:'cover',
        '&:hover' : {
            transform: 'scale(1.3)',
            backgroundImage:'linear-gradient(to bottom, transparent, rgba(0, 0, 0, 0.72)), url('+ urcupdate +')',
            zIndex: '200'
        },
    },
    section2: {
        position: 'absolute',
        marginLeft: '20vw',
        marginTop: '20vh',
        height: '35vh',
        width: '25vw',
        backgroundColor: '#000',
        zIndex: '100',
        transition: '500ms ease-in-out',
        transformOrigin: 'top left',
        backgroundImage:'url('+ urcupdate +')',
        // backgroundImage:'linear-gradient(to bottom, rgba(245, 246, 252, 0.52), rgba(117, 19, 93, 0.73))',
        backgroundSize:'cover',
        '&:hover' : {
            transform: 'scale(1.3)',
            backgroundImage:'linear-gradient(to bottom, transparent, rgba(0, 0, 0, 0.72)), url('+ urcupdate +')',
            zIndex: '200'
        },
    },
    arrow: {
        position: 'absolute',
        marginTop: '43vh',
        marginLeft: '23vw',
        fontSize: '100px'
    },
};

export default newsStyle;
