import React, {useState, useRef, useEffect} from "react";
import ReactHover from '..';
import { makeStyles } from "@material-ui/core/styles";
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import Arrowr from "@material-ui/icons/Input";

import styles from "assets/jss/material-kit-react/views/componentsSections/newsStyle.js";

import Bgvideo from "assets/videos/warrovervidf.mp4";

const useStyles = makeStyles(styles);

const optionsCursorTrueWithMargin = {
    followCursor: true,
    shiftX: 20,
    shiftY: 0
  }

export default function SectionNews() {
    const classes = useStyles();
    // const [hoverRef, isHovered] = useHover();

    return(<div>
        <div className={classes.container}>
            <h1 className={classes.title}>
                NEWS
            </h1>
            <video autoPlay loop muted className={classes.video}>
                <source src={Bgvideo} type="video/mp4" />
            </video>
            <ReactHover
                  options={optionsCursorTrueWithMargin}>
            <div className={classes.section1}>
            <ReactHover.Trigger type='trigger'>
                <h2 id="ircnews" className={classes.wrapper}>Indian Rover Challenge 2020</h2>
            </ReactHover.Trigger>
            <ReactHover.Hover type='hover'>
            </ReactHover.Hover>
            </div>
            </ReactHover>
            {/* <div className={classes.section2} ref={hoverRef}> */}
                {/* <h2 id="urcupdate" className={classes.wrapper}>Update on URC 2020 due to COVID-19</h2> */}
            {/* </div>    */}
        </div>
    </div>
    );
}

