import { container, title } from "assets/jss/material-kit-react.js";

const pillsStyle = {
  section: {
    padding: "70px 0px",
    marginBottom: '20vh'
  },
  container,
  title: {
    ...title,
    marginTop: "30px",
    minHeight: "32px",
    textDecoration: "none"
  },
  '@media (min-width: 800px)': {
    bg1: {
      position: 'absolute',
      height: '80vh',
      width: '96.1vw',
      backgroundColor: '#6B61E8',  
      clipPath: 'polygon(0 0%, 100% 0%, 11% 30%, 0% 100%)',
    },
    bg2: {
      position: 'absolute',
      height: '80vh',
      width: '96.1vw',
      backgroundImage: 'linear-gradient(#78C0FF, #ffffff)',
      clipPath: 'polygon(11% 30%, 100% 0%, 100% 100%, 0% 100%)',
    },
    border1:{
      position: 'absolute',

    },
    border2:{
      position: 'absolute',
    },
    border3:{
      position: 'absolute',
    } 
  },

'@media (min-width: 800px)': {
  bg1: {
    position: 'absolute',
    height: '80vh',
    width: '96.1vw',
    backgroundColor: '#6B61E8',  
    clipPath: 'polygon(0 0%, 100% 0%, 11% 30%, 0% 100%)',
  },
  bg2: {
    position: 'absolute',
    height: '80vh',
    width: '96.1vw',
    backgroundImage: 'linear-gradient(#78C0FF, #ffffff)',
    clipPath: 'polygon(11% 30%, 100% 0%, 100% 100%, 0% 100%)',
  },
  border1:{
    position: 'absolute',
    marginTop: '14vh',
    marginLeft: '15vw',
    height: '60vh',
    width: '65vw',
    border: 'thick solid #404040',
    borderWidth: '30px'
  },
  border2:{
    position: 'absolute',
    marginTop: '10vh',
    marginLeft: '15vw',
    height: '67vh',
    width: '66vw',
    border: 'thick solid #262626',
    borderWidth: '0 30px 30px 0',
    borderRadius: '0 0 5% 0'
  },
  border3:{
    position: 'absolute',
    marginTop: '11vh',
    marginLeft: '14vw',
    height: '20vh',
    width: '20vw',
    border: 'thick solid #262626',
    borderWidth: '40px 0 0 40px',
    borderRadius: '5% 0 0 0'
  }
}
};

export default pillsStyle;
