import React from 'react';
import cx from 'clsx';
import { makeStyles } from '@material-ui/styles';
import Box from '@material-ui/core/Box';
import Link from '@material-ui/core/Link';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Divider from '@material-ui/core/Divider';
import ArrowForwardIos from '@material-ui/icons/ArrowForwardIos';
import ModeComment from '@material-ui/icons/ModeComment';
import Favorite from '@material-ui/icons/Favorite';
import { usePushingGutterStyles } from '@mui-treasury/styles/gutter/pushing';
import { useLabelIconStyles } from '@mui-treasury/styles/icon/label';
import { useRowFlexStyles } from '@mui-treasury/styles/flex/row';

//load the medias
import img1 from "assets/img/image/rovers/2013.webp";
import img2 from "assets/img/image/rovers/2014.webp";
import img3 from "assets/img/image/rovers/2015.webp";
import img4 from "assets/img/image/rovers/2016.webp";
import img5 from "assets/img/image/rovers/2017.webp";
import img6 from "assets/img/image/rovers/2019.webp";
import img7 from "assets/img/image/rovers/2020-irc.webp";
import profilePageStyle from 'assets/jss/material-kit-react/views/profilePage';

const img = [img1, img2, img3, img4, img5, img6, img7];

const useStyles = makeStyles(() => ({
  card: {
    position: 'absolute',
    marginLeft: '9vw',
    marginTop: '10vh',
    display: 'flex',
    height: '40vh',
    width: '60vw',
    borderRadius: 16,
  },
  media: {
    marginLeft: '5vw',
    width: '23vw',
    flexShrink: 0,
    backgroundColor: '#4f4f4f',
    borderRadius: 12,
    boxShadow: '0 2px 8px 0 #c1c9d7, 0 -2px 8px 0 #cce1e9',
  },
  content: {
    width: '20vw'
  },
  heading: {
    fontSize: 40,
    fontWeight: 'bold',
    letterSpacing: '0.5px',
    marginBottom: 0,
    marginRight: "2px",
    display: 'inline-block',
  },
  body: {
    fontSize: 25,
    color: '#8a8a8a',
  },
  divider: {
    margin: '1px, 0',
  },
  textFooter: {
    fontSize: 14,
  },
  icon: {
    fontSize: '1.2rem',
    verticalAlign: 'bottom',
  },
}));

export default function SectionImageInfo(props) {
  const styles = useStyles();
  const gutterStyles = usePushingGutterStyles({ space: 1.5 });
  const labelStyles = useLabelIconStyles({ linked: true });
  const flexStyles = useRowFlexStyles();
