import React, {useState, useRef, useEffect} from "react";
import { makeStyles } from "@material-ui/core/styles";
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import Arrowr from "@material-ui/icons/Input";

import styles from "assets/jss/material-kit-react/views/componentsSections/newsStyle.js";

import Bgvideo from "assets/videos/warrovervidf.mp4";

const useStyles = makeStyles(styles);

export default function SectionNews() {
    const classes = useStyles();
    const [hoverRef, isHovered] = useHover();

    return(<div>
        <div className={classes.container}>
            <h1 className={classes.title}>
                NEWS
            </h1>
            <video autoPlay loop muted className={classes.video}>
                <source src={Bgvideo} type="video/mp4" />
            </video>
            <div className={classes.section1} ref={hoverRef}>
                <h2 className={classes.wrapper}>{hoverRef}</h2>
            </div>   
        </div>
    </div>
    );
}

function useHover() {
    const [value, setValue] = useState(false);

    const ref = useRef(null);

    const handleMouseOver = () => {
        setValue(true);

    };
    const handleMouseOut = () => {
        setValue(false);

    };

    useEffect(
      () => {
        const node = ref.current;
        if (node) {
          node.addEventListener('mouseover', handleMouseOver);
          node.addEventListener('mouseout', handleMouseOut);

          return () => {
            node.removeEventListener('mouseover', handleMouseOver);
            node.removeEventListener('mouseout', handleMouseOut);
          };
        }
      },
      [ref.current] // Recall only if ref changes
    );

    return [ref, value];
  }
