import rudrawin from "assets/img/image/news/irc-prize.webp";
import { isParenthesizedExpression } from "typescript";

const newsStyle = {
    container:{
        height: '110vh',
        width: '96vw'
    },
    video :{
        position: 'absolute',
        width: '100%', 
        zIndex: '10'
    },
    title: {
        fontFamily: 'cairo',
        position: 'absolute',
        color: '#fff',
        marginTop: '5vh',
        // marginLeft: '10vw',
        fontSize: '200px',
        fontWeight: '400',
        textStrokeWidth: '5px',
        textStrokeColor: '#fff',
        textFillColor: 'transparent',
        zIndex: '20'
    },
    section1: {
        position: 'absolute',
        marginLeft: '30vw',
        marginTop: '10vh',
        height: '35vh',
        width: '25vw',
        backgroundColor: '#000',
        zIndex: '100',
        transition: '500ms ease-in-out',
        transformOrigin: 'top left',
        backgroundImage:'url('+ rudrawin +')',
        // backgroundImage:'linear-gradient(to bottom, rgba(245, 246, 252, 0.52), rgba(117, 19, 93, 0.73))',
        backgroundSize:'cover',
        '&:hover' : {
            transform: 'scale(1.3)',
            backgroundImage:'linear-gradient(to bottom, transparent, rgba(0, 0, 0, 0.72)), url('+ rudrawin +')',
        },
        '&:hover wrappper' : {
            color: 'white'
        }
    },
    wrapper:{
        color: 'transparent'
    },

    arrow: {
        position: 'absolute',
        marginTop: '43vh',
        marginLeft: '23vw',
        fontSize: '100px'
    },
};

export default newsStyle;
