import React, {useState, useEffect, useRef } from "react";
import { makeStyles } from "@material-ui/core/styles";

import GridContainer from "components/Grid/GridContainer.js";
import styles from "assets/jss/material-kit-react/views/componentsSections/achieveTitStyle.js";

const useStyles = makeStyles(styles);

export default function AchieveTitle(props) {
    const classes = useStyles();
    const prevScrollY = useRef(0);

    const [goingUp, setGoingUp] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
          const currentScrollY = window.scrollY;
          if (prevScrollY.current < currentScrollY && goingUp) {
            setGoingUp(false);
          }
          if (prevScrollY.current > currentScrollY && !goingUp) {
            setGoingUp(true);
          }

          prevScrollY.current = currentScrollY;
          console.log(goingUp, currentScrollY);
        };

        window.addEventListener("scroll", handleScroll, { passive: true });

        return () => window.removeEventListener("scroll", handleScroll);
      }, [goingUp]);

    return (
        <GridContainer>
              <h1 className={classes.achieve}>ACHIEVEMENTS</h1>
              <h1 className={classes.achieveb}>ACHIEVEMENTS</h1>
        </GridContainer>
    );
}
