import { container, title } from "assets/jss/material-kit-react.js";

const pillsStyle = {
  section: {
    padding: "70px 0px",
    marginBottom: '20vh'
  },
  container,
  title: {
    ...title,
    marginTop: "30px",
    minHeight: "32px",
    textDecoration: "none"
  },
  bg1: {
    position: 'absolute',
    height: '80vh',
    width: '96.1vw',
    backgroundColor: '#6B61E8',  
    clipPath: 'polygon(0 0%, 100% 0%, 11% 30%, 0% 100%)',
  },
  bg2: {
    position: 'absolute',
    height: '80vh',
    width: '96.1vw',
    backgroundImage: 'linear-gradient(#78C0FF, #ffffff)',
    clipPath: 'polygon(11% 30%, 100% 0%, 100% 100%, 0% 100%)',
  },
  border1:{
    position: 'absolute',
    marginLeft: '10vw',
    height: '50vh',
    width: '70vw',
    border: 'thick solid #ffb6c1'
  },
  border2:{
    position: 'absolute',
    marginLeft: '10vw',
    height: '50vh',
    width: '70vw',
    border: 'thick solid #efd1c6'
  },
  border3:{
    position: 'absolute',
    marginLeft: '10vw',
    height: '50vh',
    width: '70vw',
    border: 'thick solid #e4a199'
  }
};

export default pillsStyle;
