import { container, title } from "assets/jss/material-kit-react.js";

const pillsStyle = {
  section: {
    height: '80vh',
    padding: "80px 10px",
    marginBottom: '20vh'
  },
  container,
  title: {
    ...title,
    marginTop: "30px",
    minHeight: "32px",
    textDecoration: "none"
  },
  bg1: {
    position: 'absolute',
    height: '80vh',
    width: '100vw',
    backgroundColor: '#6B61E8',  
    clipPath: 'polygon(0 0%, 100% 0%, 24% 45%, 0% 100%)',
  },
  bg2: {
    position: 'absolute',
    backgroundColor: '#78C0FF',
    clipPath: 'polygon(24% 45%, 100% 0%, 100% 100%, 0% 100%)',
  }
};

export default pillsStyle;
