import React from "react";
// @material-ui/core components
import { makeStyles } from "@material-ui/core/styles";
import classNames from "classnames";
// @material-ui/icons
import Info from "@material-ui/icons/Info";
import List from "@material-ui/icons/List";

// core components
import GridContainer from "components/Grid/GridContainer.js";
import GridItem from "components/Grid/GridItem.js";
import NavPills from "components/NavPills/NavPills.js";

import Quote from "components/Typography/Quote.js";
import styles from "assets/jss/material-kit-react/views/componentsSections/pillsStyle.js";

const useStyles = makeStyles(styles);


export default function SectionPills() {
  const classes = useStyles();
  return (<div>
    <div className={classes.bg1}></div>
    <div className={classes.bg2}></div> 
    <div className={classes.section}>
      <div className={classes.border1}></div>
      <div className={classes.border2}></div>
      <div className={classes.border3}></div>
      <div className={classes.main}>
        <div id="navigation-pills">
          <GridContainer>
             <GridItem style={{padding: '20px'}} xs={12} sm={15} md={13} lg={10}>
              <NavPills
                color="primary"
                horizontal={{
                  tabsGrid: { xs: 10, sm: 4, md: 3 },
                  contentGrid: { xs: 12, sm: 8, md: 9 }
                }}
                tabs={[
                  {
                    tabButton: "About Us",
                    tabIcon: Info,
                    tabContent:( <div>
                          <br></br>
                          RUDRA is the official team of SRM IST taking part in University Rover Challenge organised by Mars Society since 2013. 
                          The<br></br><br></br>annual competition is held at the Mars Desert Research Station (MDRS), Martian Analog Site, near Hanksville, Utah, USA. 
                          The<br></br><br></br> team has been successfully operational for the last 7 years and has maintained its consistency in the competition. 
                          Our Team<br></br><br></br> consists of 25-30 undergraduate students of every year from various engineering streams, working in different domains. 
                          The fun<br></br><br></br> part - R&D, is the soul and essence of Team Rudra. Brainstorming discussions are always welcome for giving birth to new ideas.
                          <br></br><br></br>
                              <Quote text="-The core of Team Rudra believes in giving an opportunity to everyone."/>
                          <br></br>   
                      </div>    
                    )
                  },
                  {
                    tabButton: "Indian Rover Challenge",
                      tabIcon: List,  
                    tabContent: (
                        <div>
                          <br></br>
                          Indian Rover Challenge is the only robotics and space exploration competition of
                          its kind in Asia-Pacific which challenges student<br></br><br></br> teams to design and build next-
                          generation Mars rovers. It is part of the 'Rover Challenge Series' of 
                          'The Mars Society', USA.<br></br><br></br> <b>Team RUDRA achieved 3rd global and 1st rank among the Asian teams in
                            IRC2020.</b>
                          <br></br><br></br>
                        </div>
                    )
                  },
                  {
                    tabButton: "University Rover Challenge",
                    tabIcon: List, 
                    tabContent: (
                      <div>
                        <br></br>
                          The University Rover Challenge (URC) is the world's premier robotics competition for college students.  Held annually in the<br></br><br></br>
                           desert of southern Utah in the United States, URC challenges student teams to design and build the next generation of Mars<br></br><br></br>
                            rovers that will one day work alongside astronauts explorin the Red Planet.
                            <br></br><br></br>
                            URC was launched in 2006, with competitions being held every summer since 2007. URC consistently draws an international<br></br><br></br> 
                            field of the most talented an promising students around. 
                              <br></br><br></br>
                              <b>Team RUDRA achieved 11th global and 2nd rank among the Asian teams in URC2019</b>
                              <br></br><br></br>
                        </div>
                    )
                  }
                ]}
              />
            </GridItem>
          </GridContainer>
        </div>
      </div>
    </div>
    </div>
  );
}
