import React, { useState } from "react";
import HorizontalTimeline from 'react-horizontal-timeline';
import { makeStyles } from "@material-ui/core/styles";


import SectionMediaCard from "./SectionAchievement.js";

import "assets/css/my.css";

import styles from "assets/jss/material-kit-react/components/timelineStyle.js";

const useStyles = makeStyles(styles);

const VALUES = ['May 28 2013', 'May 31 2014', 'May 31 2015', 'May 30 2016', 'May 30 2017', 'May 31 2019', 'Jan 17 2020'];
const pallete = [];

const useStyles2 = makeStyles(() => ({
  '@media (max-width: 800px)': {
    card:{
      width: '80vw',   
      height: '70vh', 
      marginLeft: 'auto', 
      marginRight: 'auto',
    }
  },

  '@media (min-width: 800px)': {

    card:{
      width: '60vw', 
      height: '75vh',  
      marginLeft: 'auto', 
      marginRight: 'auto',
    },
  }

}));

export default function SectionTimeline() {
    const classes = useStyles();
    const classes2 = useStyles2();
    const [value, setValue] = useState(0);
    const [previous, setPrevious] = useState(0);
    var indexInterval = setInterval(function(){
      if({value}>=5){
        setValue(0);  
      }
      setValue(value+1);
    },5000);
    return(<div>
        {/* Bounding box for the Timeline */}
        <div className={classes2.card}>
          <HorizontalTimeline
            index={value}
            indexClick={(index) => {
                setValue(index); 
                setPrevious(value);
            }}
            values={ VALUES } 
            labelWidth={100}
             />
            <SectionMediaCard index={value}/>
      </div>
      </div>);
}
