import React, { useState } from "react";
import HorizontalTimeline from 'react-horizontal-timeline';
import { makeStyles } from "@material-ui/core/styles";
import "assets/css/my.css";

import styles from "assets/jss/material-kit-react/components/timelineStyle.js";

const useStyles = makeStyles(styles);

const VALUES = ['May 28 2013', 'May 31 2014', 'May 31 2015', 'May 30 2016', 'May 30 2017', 'May 31 2019', 'Jan 17 2020'];
const Text = ['Team Rudra achieved 5th world rank and 1st Asia rank.', '2014', '2015', '2016', '2017', '2019', '2020']
export default function SectionTimeline() {
    const classes = useStyles();
    const [value, setValue] = useState(0);
    const [previous, setPrevious] = useState(0);
    return(<div>
        {/* Bounding box for the Timeline */}
        <div style={{ width: '60vw', height: '100px', marginTop: '100px', marginLeft: 'auto', marginRight: 'auto' }}>
          <HorizontalTimeline
            index={value}
            indexClick={(index) => {
                setValue(index); 
                setPrevious(value);
            }}
            values={ VALUES } 
            labelWidth={100}
             />
             <div className={classes.sectext}>   
               <p className={classes.subtext}>{Text[value]}</p>
            </div>
        </div>
      </div>);
}
