import React from 'react';
import InstagramEmbed from 'react-instagram-embed';
import { Parallax, Background } from 'react-parallax';
import { makeStyles } from '@material-ui/core/styles';

import bgImage from 'assets/img/bg8.jpg'

const useStyles = makeStyles({
    container: {
        height: '100vh',
        zIndex: '10'
    },
    outerInsta: {
        position: 'absolute',
        marginLeft: '5vw',
        marginTop: '20vh',
        width: '24vw',
        overflow: 'hidden',
        borderRadius: '2%',
        boxShadow: '3px 3px 7px 7px #888888',
        transition: '500ms ease-in-out',
        '&:hover' : {
            transform: 'scale(1.1)',
        }
    },
    instagram: {
        height: '65vh',
        width: '25vw',
        overflowY: 'scroll',
        paddingRight: '-10vw',
        transition: '500ms ease-in-out',
    },
    facebook: {
        position: 'absolute',
        marginLeft: '65vw',
        marginTop: '22vh',
        width: '10vw',
        zIndex: '1000',
        transition: '500ms ease-in-out',
        '&:hover' : {
            transform: 'scale(1.1)',
        }
    },
    facebookback: {
        position: 'absolute',
        backgroundColor: 'white',
        marginLeft: '65vw',
        marginTop: '20vh',
        height: '65vh',
        width: '26vw',
        zIndex: '100',
        borderRadius: '2%',
        boxShadow: '3px 3px 7px 7px #888888',
        transition: '500ms ease-in-out',
        '&:hover' : {
            transform: 'scale(1.1)',
        }
    },
    blog: {
        position: 'absolute',
        marginLeft: '35vw', 
        width: '25%',
        height: '65vh', 
        marginTop: '20vh',
        backgroundColor: 'white',
        borderRadius: '2%',
        boxShadow: '3px 3px 7px 7px #888888',
        transition: '500ms ease-in-out',
        '&:hover' : {
            transform: 'scale(1.1)',
        }
    },
    title:{
        position: 'absolute',
        marginLeft: '35vw',
        marginTop: '10vh',
        fontFamily: 'gotham',
        fontSize: '15vh',
        textStrokeWidth: '10px',
        textStrokeColor: '#fff',
        textFillColor: 'transparent',
    }
});

export default function SectionSocial(props) {
    const classes = useStyles();
    return (
        <Parallax
        blur={{ min: -100, max: 100 }}
        bgImage={bgImage}
        bgImageAlt="the cat"
        strength={1300}
    >
        <div className={classes.container}>
            <div className={classes.outerInsta}>
                <div className={classes.instagram}>
                    <InstagramEmbed
                        url='https://www.instagram.com/p/BtTMxnYhSlZ/'
                        maxWidth={480}
                        hideCaption={false}
                        containerTagName='div'
                        protocol=''
                        injectScript
                        onLoading={() => { }}
                        onSuccess={() => { }}
                        onAfterRender={() => { }}
                        onFailure={() => { }}
                    />
                </div>
            </div>
            <div className={classes.facebook}>
                <iframe src="https://www.facebook.com/plugins/post.php?href=https%3A%2F%2Fwww.facebook.com%2Fsrmteamrudra%2Fposts%2F2406254082726962%3A0&width=500" 
                        width="500" 
                        height="548" 
                        style={{ overflow:"hidden", borderRadius: '2%'}} 
                        scrolling="no" 
                        frameborder="0" 
                        allowTransparency="true" 
                        allow="encrypted-media">
                </iframe>
            </div>
            <div className={classes.blog}>
                <blockquote class="embedly-card"><h4><a href="https://medium.com/@srmmarsroverteam">Team Rudra - Medium</a></h4><p>Read writing from Team Rudra on Medium. RUDRA is an official robotics team of SRM Institute of Science and Technology that specializes in building mars rover prototypes.</p></blockquote>
            </div>
            <div className={classes.facebookback}></div>
            <div className={classes.title}>Social</div>
        </div>
        </Parallax>
    );
}
